commits.content:
@use '../utils/mixins' as *;

.gallery {
  @include grid;

  margin: 40px 0;

  &__item {
    width: 100%;
    position: relative;
    padding-top: 100%;

    @include for-l-mobile {
      grid-column: span 3;
      padding-top: 184px;
    }

    @include for-desktop {
      grid-column: span 6;
      padding-top: 324px;
    }

    &--small {
      @include for-l-mobile {
        grid-column: span 2;
      }

      @include for-desktop {
        grid-column: span 4;
      }
    }

    &--big {
      @include for-l-mobile {
        grid-column: span 4;
      }

      @include for-desktop {
        grid-column: span 8;
      }
    }
  }

  &__image {
    top: 0;
    width: 100%;
    height: 100%;
    position: absolute;
    object-fit: cover;
    background-position: top;
    border-radius: 4px;
    transition: 0.3s;
    max-width: 300px;
    &:hover {
      transform: scale(1.2);
      transition: 0.3s;
      z-index: 1;
    }
  }
}
