@use '../utils/mixins' as *;

.phone {

  &__label {
    font-family: Inter, Arial, sans-serif;
    font-size: 12px;
    font-weight: 700;
    line-height: 16px;
    letter-spacing: 2px;
    text-align: right;
    padding-top: 4px;
    width: 100%;

    color: #292929;
    position: absolute;
    transform: translateX(calc(-100% - 20px));
    opacity: 0;
  }

  @include for-desktop {
    // &:not(:hover) &__label {
    //   @include visually-hidden;
    // }
    &:hover &__label {
      opacity: 1;
    }
  }
}
