commits.content:
@mixin hover($_property, $_toValue) {
  transition: #{$_property} 0.3s;
  &:hover {
    #{$_property}: $_toValue;
  }
}

@mixin for-desktop {
  @media (min-width: 1260px){
    @content;
  }
}

@mixin for-l-mobile {
  @media (min-width: 744px) {
    @content;
  }
}

@mixin grid {
  display: grid;
  justify-content: center;
  grid-template-columns: repeat(2, 1frpx);
  gap: 20px;

  @include for-l-mobile {
    grid-template-columns: repeat(6, 80px);
    gap: 24px;
  }

  @include for-desktop {
    grid-template-columns: repeat(12, 63px);
    gap: 24px;
  }
}

@mixin visually-hidden {
  clip: rect(0 0 0 0);
  clip-path: inset(50%);
  height: 1px;
  overflow: hidden;
  position: absolute;
  white-space: nowrap;
  width: 1px;
}
